import { useEffect, useState } from 'react';

import getData from './Api'

import logo from './logo.svg';
import './App.css';

function App() {

  const [ productDetails, setProductDetails ] = useState([])
  const [ filter, setFilter ] = useState({ category: {}, brand: {}, gender: {} })
  const [ count, setCount ] = useState(0)
  const [ loader, setLoader ] = useState(false)
  const [ failed, setFailed ] = useState(false)

  const getProductDetails = () => {
    setLoader(true)
    getData().then((res) => {
      if (res.status === 200) {
        setProductDetails(res.data.products)
        setCount(res.data.totalCount)
        setLoader(false)
        setFailed(false)
      }
      else {
        setLoader(false)
        setFailed(true)
      }
    }).catch((err) => {
      setLoader(false)
      setFailed(true)
    })
  }

  const getFilters = () => {
    let category = new Set()
    let gender = new Set()
    let brand = new Set()
    productDetails.forEach((item) => {
      category.add(item.category)
      gender.add(item.gender)
      brand.add(item.brand)
    })
    setFilter({ category, gender, brand })
  }

  useEffect(getProductDetails, [])
  useEffect(getFilters, [ productDetails ])

