import { useEffect, useState } from 'react';

import getData from './Api'

import logo from './logo.svg';
import './App.css';

function App() {

  const [ productDetails, setProductDetails ] = useState([])
  const [ count, setCount ] = useState(0)
  const [ loader, setLoader ] = useState(false)
  const [ failed, setFailed ] = useState(false)

  const getProductDetails = () => {
    setLoader(true)
    getData().then((res) => {
      if (res.status === 200) {
        setProductDetails(res.data.products)
        setCount(res.data.totalCount)
        setLoader(false)
        setFailed(false)
      }
      else {
        setLoader(false)
        setFailed(true)
      }
    }).catch((err) => {
      setLoader(false)
      setFailed(true)
    })
  }

  const getFilters = () => {
    let filters = { category: [], gender: [], brand: [] }

    filters = productDetails.map((item, index) => {
      console.log(item.category)
      if (filters.category.indexOf(item.category) === -1 && filters.gender.indexOf(item.gender) === -1 && filters.brand.indexOf(item.brand) === -1) {
        return {
          ...filters,
          category: [
            item.category
          ],
          gender: [
            item.gender
          ],
          brand: [
            item.brand
          ]
        }
      }
      else {

      }
    })

    console.log(filters)
  }

  useEffect(getProductDetails, [])
  useEffect(getFilters, [ productDetails ])

  return (
    <div className="App">
      <header className="App-header">
        <img src={logo} className="App-logo" alt="logo" />
        <p>
          Edit <code>src/App.js</code> and save to reload.
        </p>
        <a
          className="App-link"
          href="https://reactjs.org"
          target="_blank"
          rel="noopener noreferrer"
        >
          Learn React
        </a>
      </header>
    </div>
  );
}

export default App;
