import { useEffect, useState } from 'react';

import getData from './Api'

import logo from './logo.svg';
import './App.css';

function App() {

  const [ productDetails, setProductDetails ] = useState([])
  const [ filter, setFilter ] = useState({ category: [], brand: [], gender: [] })
  const [ filteredData, setFilteredData ] = useState([])
  const [ count, setCount ] = useState(0)
  const [ loader, setLoader ] = useState(false)
  const [ failed, setFailed ] = useState(false)

  const getProductDetails = () => {
    setLoader(true)
    getData().then((res) => {
      if (res.status === 200) {
        setProductDetails(res.data.products)
        setCount(res.data.totalCount)
        setLoader(false)
        setFailed(false)
      }
      else {
        setLoader(false)
        setFailed(true)
      }
    }).catch((err) => {
      setLoader(false)
      setFailed(true)
    })
  }

  const getFilters = () => {
    let category = new Set()
    let gender = new Set()
    let brand = new Set()
    productDetails.forEach((item) => {
      category.add(item.category)
      gender.add(item.gender)
      brand.add(item.brand)
    })

    setFilter({ category: Array.from(category), gender: Array.from(gender), brand: Array.from(brand) })
  }

  useEffect(getProductDetails, [])
  useEffect(getFilters, [ productDetails ])

  const filterData = (filter, type) => {
    console.log(filter, type)
    setFilteredData(productDetails.filter(item => item[ type ] === filter))
  }

  return (
    <div className="App">
      <h1>Category</h1>
      {filter.category && filter.category.map((item) => (
        <div onClick={() => filterData(item, 'category')} >
          {item}
        </div>
      ))
      }
      <h1>Gender</h1>
      {filter.gender && filter.gender.map((item) => (
        <div onClick={() => filterData(item, 'gender')}>
          {item}
        </div>
      ))
      }
      <h1>Brand</h1>
      {filter.brand && filter.brand.map((item) => (
        <div onClick={() => filterData(item, 'brand')}>
          {item}
        </div>
      ))
      }


      <div>

      </div>
    </div>
  );
}

export default App;
